let debugFileNames = flow.get("debugFileNames");
let doOnce = true;
let msgCount = flow.get("msgCount");
let fileName = flow.get("$parent.debugFileName");
if (!fileName) fileName =  msg.debugFileName;
if (!fileName) fileName = "/devices/debug.csv"

if (debugFileNames[fileName]) {
	doOnce = false;
} else {
	debugFileNames[fileName] = true;
	doOnce = true;
	flow.set("debugFileNames",debugFileNames);
}

const fs = global.get("fs");
const path = global.get("path")
if (doOnce) {
	let baseName = path.join(path.dirname(fileName),path.basename(fileName,path.extname(fileName)))
    node.warn( [ "[" + node.name + "] " + "[Info] " + "Debug Filename is " + fileName]);
	let cnt = 0;
	let rotator = "_" + (++cnt) + path.extname(fileName);
	while (fs.existsSync(baseName + rotator)) {
		rotator = "_" + (++cnt) + path.extname(fileName);
	}
    try {
		if (fs.existsSync(fileName)) {
			node.warn( [ "[" + node.name + "] " + "[Info] ", 
					"Output File old file renamed from  " + fileName + "to " + baseName + rotator ]);
			fs.renameSync(fileName,baseName + rotator);
			//fs.unlinkSync(fileName)
		}
    } catch(err) {
		node.warn( [err, "[" + node.name + "] " + "[Info] ", "Output File does not exit " + fileName ]);
    }
}
let cmds = [];
let tmpDate = new Date().toLocaleDateString("en-gb");
let tmpTime = new Date().toLocaleTimeString("en-gb");
let cols = msg.columns 
if (cols) {
    cols = "Date,Time, Topic," + cols;
} else {
    cols = "Date,Time, Topic,Commands";
}
try {
	if ( (msg.payload) && typeof(msg.payload) == "object" ) {
		if (Array.isArray(msg.payload)) {
			msg.payload.forEach( (cmdo) => {
				if ( (cmdo) && ( typeof(cmdo) == "object" ) ) {
					//cmds.push(cmdo.cmd + ":" + cmdo.payload)
					Object.keys( cmdo ).forEach( (key) => {
						cmds.push(key + ":" + cmdo[key]);
					})
				} else if (cmdo) {
					//cmds.push(typeof(cmdo) + ":" + cmdo)
					cmds.push(cmdo)
				}
			});
		} else {
			Object.keys( msg.payload ).forEach( (key) => {
				cmds.push(key + ":" + msg.payload[key]);
			})
		}
	} else {
		flow.set("msgCount",++msgCount);
		return [	{topic:msg.topic,
					columns: cols,
					filename: fileName,
					payload: [tmpDate,tmpTime,msg.topic, msg.payload ]
				},
				{topic:"status", payload: ({fill: "green",text: fileName + " (" + msgCount + ")msgs"})}
				]
	}
} catch (err) {
	node.error( [ "[" + node.name + "] " + "[Error] " + err, err, msg]);
}
flow.set("msgCount",++msgCount);
//Date,Topic,Pathway,Commands
return [	{topic:msg.topic,
				columns: cols,
				filename: fileName,
				payload: [tmpDate,tmpTime, msg.topic,...cmds ]
			},
				{topic:"status", payload: ({fill: "green",text: fileName + " (" + msgCount + ")msgs"})}
		]

initialize:
// Code added here will be run once
// whenever the node is started.
//  const path = './file.txt'

//flow.set("DoOnce",true)
flow.set("msgCount",0);
flow.set("debugFileNames",{});

func:
//Table Manager
let gDebug = global.get("gDebug");
//gDebug=true;
let sockets = context.get("sockets")
if (!sockets) sockets = {};
let tabs = context.get("tabs");
if (!tabs) tabs = {};
let tableInit = context.get("tableInit");
//node.warn(["DEBUG",msg])
if (!tableInit) {
	if (gDebug) node.warn( [ "[" + node.name + "] " + "[Info] ","tableInit is undefined - setting up",msg]);
	tableInit = {ready: false, details:null};
	context.set("tableInit",tableInit);
}
if ( (msg.topic=="initTable") && (msg.hasOwnProperty("tableInit")) ) {
	tableInit = msg.tableInit;
	tableInit.details = "$parent." + tableInit.details
	context.set("tableInit",tableInit);
}
if ( (typeof(msg.payload) == "string") && (!msg.topic) && 
		( (msg.payload=="lost") || (msg.payload=="change") || (msg.payload=="connect") ) ) {
	// From UI Control - refresh or tab change
	if ( (msg.payload=="connect")  || (msg.payload=="change") ) {
		sockets[msg.socketid] = true;
		tabs[msg.socketid] = [];
	} else {
		sockets[msg.socketid] = false;
		tabs[msg.socketid] = [];
	}
	if (msg.payload=="change") {
		tabs[msg.socketid].push(msg.name);
	}
	context.set("sockets", sockets);
	context.set("tabs", tabs)
}
if ( (msg.topic == "RefreshTable") &&  (!context.get("refreshableTable")) ) {
	return [null, null, null];
}

if (!tableInit.ready) {
	if (msg.topic) node.error( [ "[" + node.name + "] " + "[Error] tableInit Not ready no processing done", msg]);
	return [null, null, null];
}

let tableDetails = flow.get(tableInit.details);
context.set("refreshableTable", tableDetails.refreshableTable);
if (tableDetails === undefined) {
	node.error( [ "[" + node.name + "] " + "[Error] ","tableDetails is undefined"]);
	return [null, null, null];
}

let statusMsg = {};
let updateMsg = [];
let tableMsg = {};
let updateMsgBase = {
						"topic": "TableUpdate",
						"tableDatabase": tableDetails.tableDatabase,
						"tableDatabaseName": tableDetails.tableDatabaseName,
						"tableInit": tableInit
						};

if (tableDetails.lastID === undefined) {
	tableDetails.lastID = 0;
	tableDetails.tableData.forEach( (row,index) => {if (tableDetails.lastID < row.id) tableDetails.lastID = row.id});
	if (gDebug) node.warn( [ "[" + node.name + "][" + tableDetails.tableDatabaseName + "]" + "[Info] ","lastID set to " + tableDetails.lastID]);
	flow.set(tableInit.details,tableDetails);
}
if ( (!tableDetails.tableDatabase) || (!tableDetails.tableDatabaseName) ){
	node.warn( [ "[" + node.name + "][" + tableDetails.tableDatabaseName + "]" + "[Info] ","tableDatabase is undefined",tableDetails.tableDatabaseName, tableDetails.tableDatabase]);
}

// find the index for a row in tableData for a given index (id)
function checkIndex(id) {
    let matchRow=-1
    tableDetails.tableData.forEach(function (row,index){
        if (row.id === id){
            matchRow=index;
            return matchRow;
        }
    })
    return matchRow;
}

// flat merge one row 
function mergeRow(dest,source) {
    Object.keys(source).forEach(function(key) {
        dest[key]=source[key];
    })
}

//merge or add one or many rows into tableData 
function mergeData(newData,toTop) {
    newData.forEach(function (item,index) {
        if (gDebug) node.warn( [ "[" + node.name + "][" + tableDetails.tableDatabaseName + "]" + "[Info] [findIndex] ",item,index]);
        let row=checkIndex(item.id);4
        if (row<0) { // row do not existst in tableData
            if (toTop) {
                tableDetails.tableData.push(item);
				if (gDebug) node.warn( [ "[" + node.name + "][" + tableDetails.tableDatabaseName + "]" + "[Info] [findIndex] "," new row top ",item,index]);
				//node.status({fill:"green",shape:"dot",text: tableInit.details + " Added newRow @ top"});
				statusMsg = {payload: {fill:"green",shape:"dot",text: tableInit.details.replace("$parent.","") + " Added newRow @ top"}};
				node.status(statusMsg);
            } else {
                tableDetails.tableData.unshift(item);
				if (gDebug) node.warn( [ "[" + node.name + "][" + tableDetails.tableDatabaseName + "]" + "[Info] [findIndex] "," new row bottom ",item,index]);
				statusMsg = {payload: {fill:"green",shape:"dot",text: tableInit.details.replace("$parent.","") + " Added newRow @ bottom"}};
				node.status(statusMsg);
				//node.status({fill:"green",shape:"dot",text: tableInit.details + " Added newRow @ bottom"});
            }
            return;
        } else { // row exists so update
            mergeRow(tableDetails.tableData[row],item);
			if (gDebug) node.warn( [ "[" + node.name + "][" + tableDetails.tableDatabaseName + "]" + "[Info] [findIndex] "," row updated ",item,index]);
			statusMsg = {payload: {fill:"green",shape:"dot",text: tableInit.details.replace("$parent.","") + "  row updated "}};
			node.status(statusMsg);
			//node.status({fill:"green",shape:"dot",text: tableInit.details + "  row updated "});
            return;
        }
    });
}
if (msg.topic == "UpdateTable") {
	if (gDebug) node.warn( [ "[" + node.name + "][" + tableDetails.tableDatabaseName + "]" + "[Info] ","Update Table Rec'd", msg.payload, msg, tableDetails]);
	if ( (!msg.payload) || (!msg.payload.cmd) || (!msg.payload.value) ) {
		node.error( [ "[" + node.name + "][" + tableDetails.tableDatabaseName + "]" + "[Error] ","Update Table invalid payload", msg.payload, msg, tableDetails]);
		statusMsg = {payload: {fill:"red",shape:"ring",text: tableInit.details.replace("$parent.","") + " Update Table invalid payload"}};
		return [null,null,statusMsg]
	}
	let found = -1;
	if (!msg.payload.value[tableDetails.tableKeys[0]]) {
		node.error( [ "[" + node.name + "][" + tableDetails.tableDatabaseName + "]" + "[Error] ","Update Table no key in payload", msg.payload, msg, tableDetails]);
		statusMsg = {payload: {fill:"red",shape:"ring",text: tableInit.details.replace("$parent.","") + " Update Table no key in payload"}};
		return [null,null,statusMsg]
	}
	tableDetails.tableData.forEach(function (row,index){
		if ( row[tableDetails.tableKeys[0]] == msg.payload.value[tableDetails.tableKeys[0]] ) found = index
	});
	let clonedMsg = RED.util.cloneMessage(msg);
	if (msg.payload.cmd == "addRow") {
		if (found !=-1) {
			node.error( [ "[" + node.name + "][" + tableDetails.tableDatabaseName + "]" + "[Error] ","Update Table add row exists", msg.payload, msg, tableDetails]);
			statusMsg = {payload: {fill:"red",shape:"ring",text: tableInit.details.replace("$parent.","") + " Update Table add row exists"}};
			return [null,null,statusMsg]
		}
		if (gDebug) node.warn(["[" + node.name + "] [UpdateTable][Info] ","Adding Row ", msg.payload, msg]);
		tableDetails.lastID = tableDetails.lastID + 1;
		clonedMsg.payload.value.id = tableDetails.lastID;
		tableDetails.tableData.push(clonedMsg.payload.value);
		statusMsg = {payload: {fill:"green",shape:"dot",text: tableInit.details.replace("$parent.","") + " Updated Table with new row"}};
		tableMsg = {topic: "updateData", payload:{command: "updateData", arguments: [[clonedMsg.payload.value]]}};
		return [tableMsg,null,statusMsg]
	} else if (msg.payload.cmd == "updateRow") {
		if (found ==-1) {
			node.error( [ "[" + node.name + "][" + tableDetails.tableDatabaseName + "]" + "[Error] ","Update Table updateRow doesn't exist", msg.payload, msg, tableDetails]);
			statusMsg = {payload: {fill:"red",shape:"ring",text: tableInit.details.replace("$parent.","") + " Update Table updateRow doesn't exist"}};
			return [null,null,statusMsg]
		}
		Object.keys(msg.payload.value).forEach( (ele) => {
			if (ele != tableDetails.tableKeys[0]) tableDetails.tableData[found][ele] = msg.payload.value[ele]
		});
		clonedMsg.payload.value.id = tableDetails.tableData[found].id;
		statusMsg = {payload: {fill:"green",shape:"dot",text: tableInit.details.replace("$parent.","") + " Updated Table updated row"}};
		tableMsg = {topic: "updateData", payload:{command: "updateData", arguments: [[clonedMsg.payload.value]]}};
		return [tableMsg,null,statusMsg]
	} else if (msg.payload.cmd == "deleteRow") {
		if (found ==-1) {
			node.error( [ "[" + node.name + "][" + tableDetails.tableDatabaseName + "]" + "[Error] ","Update Table DeleteRow doesn't exist", msg.payload, msg, tableDetails]);
			statusMsg = {payload: {fill:"red",shape:"ring",text: tableInit.details.replace("$parent.","") + " Update Table DeleteRow doesn't exist"}};
			return [null,null,statusMsg]
		}
		clonedMsg.payload.value.id = tableDetails.tableData[found].id;
		statusMsg = {payload: {fill:"green",shape:"dot",text: tableInit.details.replace("$parent.","") + " Updated Table Deleted row"}};
		return [null,null,statusMsg]
	} else {
		node.error( [ "[" + node.name + "][" + tableDetails.tableDatabaseName + "]" + "[Error] ","Update Table invalid cmd", msg.payload, msg, tableDetails]);
		statusMsg = {payload: {fill:"red",shape:"ring",text: tableInit.details.replace("$parent.","") + " Update Table invalid cmd"}};
		return [null,null,statusMsg]
	}		
}
if (msg.topic == "RefreshTable") {
	if (gDebug) node.warn( [ "[" + node.name + "][" + tableDetails.tableDatabaseName + "]" + "[Info] ","Refresh Table Rec'd", msg.payload, msg, tableDetails]);
	const connd = Object.values(sockets).reduce((pv,cv, ind) => {return (pv||cv)}, false);
	if (connd) {
		statusMsg = {fill:"green",shape:"ring",text: "Clients connected"};	
		const tabbed = Object.values(tabs).reduce((pv,cv,ind) => { return (pv||cv.includes(tableDetails.tableTab))}, false);
		if (tabbed) {
			let nowDate = new Date();
			msg.payload = tableDetails.tableData.map( (ent) => {
				return({refreshTime:nowDate,...ent});
			});
			msg.refreshDate = nowDate.getDate() + "/" + (nowDate.getMonth() + 1) + 
					"/" + nowDate.getFullYear() + " " + nowDate.getHours() + ":" + nowDate.getMinutes() + ":" + nowDate.getSeconds();
			statusMsg = {payload: {fill:"green",shape:"dot",text: tableInit.details.replace("$parent.","") + " Refresh Table issued"}};
			let msgs = [];
			msgs.push(msg);
			if (tableDetails.sortField) {
				msgs.push({topic:"sortTable",refreshDate: msg.refreshDate,
						payload:{"command": "setSort","arguments":
							[[{"column": tableDetails.sortField, "dir": tableDetails.sortDir}],
								true],"returnPromise": false}});
			}
			node.status(statusMsg);
			return [msgs, null, statusMsg]
		} else {
			statusMsg = {payload: {fill:"blue",shape:"ring",text: tableInit.details.replace("$parent.","") + " Refresh Table No Client Tabs"}};
			node.status(statusMsg)
			return [null, null, statusMsg]
		}
	} else {
		statusMsg = {payload: {fill:"red",shape:"ring",text: tableInit.details.replace("$parent.","") + " Refresh Table Nobody Connected"}};
		node.status(statusMsg)
		return [null, null, statusMsg]
	}
}
if ( (msg.topic == "headerClicked") && (msg.hasOwnProperty("ui_control")) ) {
	tableDetails.sortField = msg.ui_control.column;
	tableDetails.sortDir = "asc";
	const newSortField = msg.ui_control.column;
	const getSort = (msg.ui_control.getSorters) ? msg.ui_control.getSorters.find( (ele) => ele.field == newSortField ) : null;
	if ( (getSort) && (getSort.dir == "asc") ) tableDetails.sortDir = "desc"; 
	flow.set(tableInit.details,tableDetails);
	statusMsg = {payload:{fill:"green",shape:"dot",text: tableInit.details.replace("$parent.","") + " Table was sorted:" + msg.ui_control.column}}
	node.status(statusMsg);
	return [null, null, statusMsg]
}
if ( (msg.hasOwnProperty("payload")) && 
	( msg.hasOwnProperty("ui_control") ) && ( msg.ui_control.hasOwnProperty("callback") )) {
	if (msg.ui_control.callback == "cellEdited") {
	// From Table, edit field
		if ( (!msg.hasOwnProperty("field")) || (!msg.hasOwnProperty("id")) ) {
			node.error( [ "[" + node.name + "][" + tableDetails.tableDatabaseName + "]" + "[Error] ","field and/or id missing ", msg, tableDetails.tableData]);
			//node.status({fill:"red",shape:"ring",text: tableInit.details + " cellEdit field and/or id missing "});
			statusMsg = {payload: {fill:"red",shape:"ring",text: tableInit.details.replace("$parent.","") + " cellEdit field and/or id missing "}};
			node.status(statusMsg);
			return [null, null, statusMsg]
		}
		let ind = checkIndex(msg.id);
		if (ind==-1) {
			node.error( [ "[" + node.name + "][" + tableDetails.tableDatabaseName + "]" + "[Error] ","id not in tableData ", msg, tableDetails.tableData]);
			//node.status({fill:"red",shape:"ring",text: tableInit.details + " id not in tableData  "});
			statusMsg = {payload: {fill:"red",shape:"ring",text: tableInit.details.replace("$parent.","") + " id not in tableData  "}};
			node.status(statusMsg);
			return [null, null, statusMsg]
		}
		if (gDebug) node.warn( [ "[" + node.name + "][" + tableDetails.tableDatabaseName + "]" + "[Info] [cellEdited] amending ",tableDetails.tableData[ind],msg.field,msg.payload]);

		let oldField = {};
		oldField[msg.field] = msg.oldValue;
		updateMsg.push({...updateMsgBase, "tableOldField": oldField, tableUpdateCommand: "cellEdited",
							payload: tableDetails.tableData[ind]});
		tableDetails.tableData[ind][msg.field] = msg.payload;
		flow.set(tableInit.details,tableDetails);
		//node.status({fill:"green",shape:"dot",text: tableInit.details + " User edited id:" + msg.id + ",row:" + ind + ",field:" + msg.field});
		statusMsg = {payload: {fill:"green",shape:"dot",text: tableInit.details.replace("$parent.","") + " User edited id:" + msg.id + ",row:" + ind + ",field:" + msg.field}};
		node.status(statusMsg)
		return [null, updateMsg, statusMsg]
	} else if (msg.ui_control.callback == "rowDeleted") {
		if (gDebug) node.warn( [ "[" + node.name + "][" + tableDetails.tableDatabaseName + "]" + "[Info] [cellEdited] ", "Deleting Row " + msg.rowID,msg]);
		let ind = checkIndex(msg.rowID);
		if (ind==-1) {
			node.warn( [ "[" + node.name + "][" + tableDetails.tableDatabaseName + "]" + "[Error] ","id not in tableData for deleteRow ", msg]);
			//node.status({fill:"red",shape:"ring",text: tableInit.details + " id not in tableData  "});
			statusMsg = {payload: {fill:"red",shape:"ring",text: tableInit.details.replace("$parent.","") + " id not in tableData"}};
			node.status(statusMsg);
			return [null, null, statusMsg]
		}
		if (gDebug) node.warn( [ "[" + node.name + "][" + tableDetails.tableDatabaseName + "]" + "[Info] [cellEdited] ", 
								"Deleting ind=" + ind + " ",tableDetails.tableData[ind][tableDetails.tableKeys[0]],
								msg.rowID,tableDetails.tableData[ind],msg]);
		updateMsg.push({...updateMsgBase, tableUpdateCommand: "deleteRow", payload: tableDetails.tableData[ind]});
		tableDetails.tableData.splice(ind,1);
		flow.set(tableInit.details,tableDetails);
		return [null, updateMsg, statusMsg]
	} else if (msg.ui_control.callback == "buttonClicked") {
		//Just passing on at the moment
		let ind = checkIndex(msg.id);
		if (gDebug) node.warn( [ "[" + node.name + "][" + tableDetails.tableDatabaseName + "]" + "[Info] [cellEdited] ", "Button Clicked " + msg.rowID,msg]);
		statusMsg = {payload: {fill:"green",shape:"dot",text: tableInit.details.replace("$parent.","") + " Button Clicked " + msg.id + ",row:" + ind}};
		return [msg, null, statusMsg]
	}
	node.warn( [ "[" + node.name + "][" + tableDetails.tableDatabaseName + "]" + "[Error] Unknown Callback Value", msg]);
	statusMsg = {payload: {fill:"red",shape:"ring",text: tableInit.details.replace("$parent.","") + " Unknown callback " + msg.ui_control.callback}};
	node.status(statusMsg);
	return [null, null, statusMsg]
}
if ( (typeof(msg.payload) == "object") && ( Array.isArray(msg.payload) ) ) {
	// Loading Table
	if (gDebug) node.warn( [ "[" + node.name + "][" + tableDetails.tableDatabaseName + "]" + "[Info] ","Loading Table ",msg.ui_control]);
	tableDetails.tableData=RED.util.cloneMessage(msg.payload); 
	tableDetails.lastID = 0;
	tableDetails.tableData.forEach( (row,index) => {if (tableDetails.lastID < row.id) tableDetails.lastID = row.id});
	if (gDebug) node.warn( [ "[" + node.name + "][" + tableDetails.tableDatabaseName + "]" + "[Info] ","lastID set to " + tableDetails.lastID]);
	flow.set(tableInit.details,tableDetails);
	//node.status({fill:"green",shape:"dot",text: tableInit.details + " table replaced "+msg.payload.length+" rows"});
	statusMsg = {payload: {fill:"green",shape:"dot",text: tableInit.details.replace("$parent.","") + " table replaced "+msg.payload.length+" rows"}};
	node.status(statusMsg);
	return [msg, null, statusMsg]
}
if ( (typeof(msg.payload) == "object") && ( msg.hasOwnProperty("row") ) ) {
	// From Table, click on cell (row)
	if (gDebug) node.warn( [ "[" + node.name + "][" + tableDetails.tableDatabaseName + "]" + "[Info] ","User clicked on row ",msg.row,msg]);
	//node.status({fill:"green",shape:"dot",text: tableInit.details + " User Clicked on Row " + msg.row});
	statusMsg = {payload: {fill:"green",shape:"dot",text: tableInit.details.replace("$parent.","") + " User Clicked on Row " + msg.row}};
	node.status(statusMsg);
	return [null, null, statusMsg]
}

if ( (msg.topic) && (msg.topic == "success") && ( msg.hasOwnProperty("ui_control") ) ) {
	// From Table - promise return
	if (gDebug) node.warn(["[" + node.name + "] [Info] ","Promise Return ",msg.ui_control,msg]);
	//node.status({fill:"green",shape:"dot",text: tableInit.details + " Successful Promise Returned:" + msg.ui_control.callback});
	return [null, null, null]
}

if ( (typeof(msg.payload) == "string") && (!msg.topic) && 
		( (msg.payload=="lost") || (msg.payload=="change") || (msg.payload=="connect") ) ) {
	// From UI Control - refresh or tab change
	if (msg.payload == "change") {
		if (gDebug) node.warn( [ "[" + node.name + "][" + tableDetails.tableDatabaseName + "]" + "[Info] ","UI Control Change issued ", msg.payload, msg, tableDetails]);
		msg.topic = "changeRefresh";
		msg.payload = tableDetails.tableData;
		//node.status({fill:"green",shape:"dot",text: tableInit.details + " UI Control Change issued "});
		statusMsg = {payload: {fill:"green",shape:"dot",text: tableInit.details.replace("$parent.","") + " UI Control Change issued"}};
		node.status(statusMsg);
		return [msg, null, statusMsg]
	}
	if (gDebug) node.warn( [ "[" + node.name + "][" + tableDetails.tableDatabaseName + "]" + "[Info] ","UI Control ", msg.payload, msg]);
	return [null, null, null]
}

if ( (typeof(msg.payload) == "object") && ( msg.payload.hasOwnProperty("command") ) ) {
	// Sending Command to table addRow,  updateData
	let clonedMsg = RED.util.cloneMessage(msg);
	if (gDebug) node.warn(["[" + node.name + "][addRow] [Info] ","Command sent to table ", msg.payload, msg]);
	if (msg.payload.command == "addRow") {
		if (gDebug) node.warn(["[" + node.name + "] [addRow] [Info] ","Adding Row ", msg.payload, msg]);
		clonedMsg.payload.arguments[0].forEach( (row,index) => {
			tableDetails.lastID = tableDetails.lastID + 1;
			row.id = tableDetails.lastID;
			tableDetails.tableKeys.forEach( (tkey) => {
				if (tkey != "id" ) row[tkey] = row[tkey] + "(" + row.id + ")";
			});
			//row[tableDetails.tableKeys[0]] = row[tableDetails.tableKeys[0]] + "(" + row.id + ")";
			if (gDebug) node.warn(["[" + node.name + "] [addRow] [Info] ","In CopiedMsg ", row, msg]);
            if (msg.payload.arguments[1]) {
				tableDetails.tableData.push(row);
				if (gDebug) node.warn(["[" + node.name + "] [addRow] [Info] ","new row top ",row,index]);
				//node.status({fill:"green",shape:"dot",text: tableInit.details + " addRow:newRow @ top"});
				statusMsg = {payload: {fill:"green",shape:"dot",text: tableInit.details.replace("$parent.","") + " addRow:newRow @ top"}};
            } else {
				tableDetails.tableData.unshift(row);
				if (gDebug) node.warn(["[" + node.name + "] [addRow] [Info] ","new row bottom ",row,index]);
				//node.status({fill:"green",shape:"dot",text: tableInit.details + " addRow:newRow @ bottom"});
				statusMsg = {payload: {fill:"green",shape:"dot",text: tableInit.details.replace("$parent.","") + " addRow:newRow @ bottom"}};
            }
			updateMsg.push({...updateMsgBase, tableUpdateCommand: clonedMsg.payload.command, payload: row});
		});
		node.status(statusMsg);
		flow.set(tableInit.details,tableDetails);
		return [clonedMsg, updateMsg, statusMsg]
	}
	if ( (msg.payload.command == "updateOrAddData") || (msg.payload.command == "updateData") ) {
		msg.payload.arguments[0].forEach( (row,index) => {
			let ind = checkIndex(row.id);
			if (ind != -1) {
				updateMsg.push({...updateMsgBase, tableUpdateCommand: msg.payload.command, payload: tableDetails.tableData[ind]});
			} else {
				node.error(["[" + node.name + "][updateOrAddData] [error] ","row doesn't exist in tableData ", row, msg]);
			}
		});
		mergeData(msg.payload.arguments[0]);
		flow.set(tableInit.details,tableDetails);
		//node.status({fill:"green",shape:"dot",text: tableInit.details + " updateOrAddData:"});
		statusMsg = {payload: {fill:"green",shape:"dot",text: tableInit.details.replace("$parent.","") + " updateOrAddData:"}};
		node.status(statusMsg);
		return [msg, updateMsg, statusMsg]
	}
	if (msg.payload.command == "deleteRow") {
		let row=checkIndex(msg.payload.arguments[0]);
		tableDetails.tableData.splice(row,1);
		flow.set(tableInit.details,tableDetails);
		node.error(["[" + node.name + "][deleteRow] [Error] Did not expect to get here", msg]);
		//node.status({fill:"green",shape:"dot",text: tableInit.details + " deleteRow:" + row + " deleted"});
		statusMsg = {payload: {fill:"green",shape:"dot",text: tableInit.details.replace("$parent.","") + " deleteRow:" + row + " deleted"}};
		node.status(statusMsg);
		return [msg, updateMsg, statusMsg]
	}
	//node.status({fill:"red",shape:"ring",text:msg.payload.command + " unknown!"});
	statusMsg = {payload: {fill:"red",shape:"ring", text:msg.payload.command + " unknown!"}};
	node.status(statusMsg);
	node.error(["[" + node.name + "] [error] unknown command ",msg.payload.command]);
	return [null, null, statusMsg]
}
