function recordLookup(rec) {
	if (tableDetails.tableType=="object") {
		let keys = tableDetails.tableKeys;
		if (!keys) {
			node.error( [ "[" + node.name + "][" + tableDetails.tableDatabaseName + "]" + "[Error] Keys not set " ,keys, tableDetails]);
			return -1;
		}
		if (!globalData[rec[keys[0]]]) return -1;
		return rec[keys[0]];
	}
	//we have an array with potentially multiple match conditions
	let ind = globalData.findIndex( (ele) => {
		let finder = true;
		Object.keys(rec).forEach( (key) => {
			if ( ( (key != "id") || (ele[key]) )  && (ele[key] != rec[key]) ) finder = false;
			//if (key != "id") if (ele[key] != rec[key] ) finder = false;
		});
		return finder;
	});
	return ind; 
}
let found;
