	let prevRec = RED.util.cloneMessage(msg.payload);// or spread{...msg.payload}; //should we use red.clone?
	Object.keys(msg.tableOldField).forEach( (key) => { prevRec[key] = msg.tableOldField[key] } );
	found = recordLookup(prevRec);
	if (gDebug) node.warn( [ "[" + node.name + "][" + tableDetails.tableDatabaseName + "]" + "[Info] " +  " Lookup using prevRec ",prevRec,msg,tableDetails]);
} else {
	found = recordLookup(msg.payload)
	if (gDebug) node.warn( [ "[" + node.name + "][" + tableDetails.tableDatabaseName + "]" + "[Info] " +  " Lookup using payload ",msg,tableDetails]);
}
if (gDebug) node.warn( [ "[" + node.name + "][" + tableDetails.tableDatabaseName + "]" + "[Info] " +  " Received Msg found=" + found + " " + msg.tableUpdateCommand,msg,tableDetails]);
if (msg.tableUpdateCommand == "addRow") {
	if ( (found != -1) || (globalData[found] != null) ) {
		node.error( [ "[" + node.name + "][" + tableDetails.tableDatabaseName + "]" + "[Error] " +  msg.tableUpdateCommand +
					"Device already exists in " + tableDetails.tableDatabaseName + " found=" + found, globalData[found],tableDetails,msg]);
