		" Device already in " + tableDetails.tableDatabaseName + " "  + msg.payload[tableDetails.tableKeys[0]]}};
		node.status(statusMsg);
		return [null, statusMsg];	
	}
	let {id, ...sms} = msg.payload; // Update all fields except id
	if (tableDetails.tableType == "array" ){
		globalData.unshift(sms);
	} else {
		delete sms[tableDetails.tableKeys[0]]; // get rid of key if object
		globalData[msg.payload[tableDetails.tableKeys[0]]] = sms;
	}
} else {
	if ( (msg.tableUpdateCommand == "deleteRow") || (msg.tableUpdateCommand == "cellEdited") ||
					(msg.tableUpdateCommand == "updateData") || (msg.tableUpdateCommand == "updateOrAddData") ) {
		if (found == -1) {
			node.error( [ "[" + node.name + "][" + tableDetails.tableDatabaseName + "]" + "[Error] " +  msg.tableUpdateCommand +
						" Device not in " + tableDetails.tableDatabaseName, tableDetails,msg]);
			//node.status({fill:"red",shape:"ring",text: tableInit.details + " " + msg.tableUpdateCommand + " Device not in  " + tableDetails.tableDatabaseName + " "  + msg.payload[tableDetails.tableKeys[0]]});
			statusMsg = {payload: {fill:"red",shape:"ring",text: tableInit.details.replace("$parent.","") + " " + msg.tableUpdateCommand + 
			" Device not in " + tableDetails.tableDatabaseName + " "  + msg.payload[tableDetails.tableKeys[0]]}};
			node.status(statusMsg);
			return [null, statusMsg];								
		}
		if (msg.tableUpdateCommand == "deleteRow") {
			if (tableDetails.tableType == "array" ){
				globalData.splice(found,1);
			} else {
				globalData[msg.payload[tableDetails.tableKeys[0]]] = null;
			}
		} else {
			let {id, ...sms} = msg.payload; // Update all fields except id
			if (gDebug) node.warn( [ "[" + node.name + "][" + tableDetails.tableDatabaseName + "][Info]Updating Record found=" + 
										found,msg,tableDetails,globalData[found],sms]);
			if (tableDetails.tableType == "object" ) {
				let incomingKey = sms[tableDetails.tableKeys[0]]
				delete sms[tableDetails.tableKeys[0]];
				if (incomingKey != found) {
					if (gDebug) node.warn( [ "[" + node.name + "][" + tableDetails.tableDatabaseName + "]" + "[Info] " +  " Key Change found=" + found + " inc=" + incomingKey,msg,tableDetails]);
					globalData[incomingKey] = {};
					Object.keys(sms).forEach((key) => {
						globalData[incomingKey][key] = globalData[found][key];
					});
					globalData[found] = null;
					delete globalData.found
				} else {
					Object.keys(sms).forEach((key) => {
						globalData[found][key] = sms[key];
					});
				}
			} else {
				Object.keys(sms).forEach((key) => { 
					globalData[found][key] = sms[key];
					if (gDebug) node.warn( [ "[" + node.name + "][" + tableDetails.tableDatabaseName + "][Info]Updating key=" + key,
											globalData[found],sms]);
				});
			}
		}
	} else {
		node.error( [ "[" + node.name + "][" + tableDetails.tableDatabaseName + "]" + "[Error] ", "Msg topic not = TableUpdate ",tableDetails,msg]);
		//node.status({fill:"red",shape:"ring",text: tableInit.details + " " + "Unknown Command  " + msg.tableUpdateCommand });
		statusMsg = {payload: {fill:"red",shape:"ring",text: tableInit.details.replace("$parent.","") + " " + "Unknown Command  " + msg.tableUpdateCommand }};
		node.status(statusMsg);
		return [null, statusMsg];	
	}
}
