//node.status({fill:"green",shape:"dot",text: tableInit.details + " " + msg.tableUpdateCommand + " updated " + msg.payload[tableDetails.tableKeys[0]]});	
statusMsg = {payload: {fill:"green",shape:"dot",text: tableInit.details.replace("$parent.","") + " " + msg.tableUpdateCommand + " updated " + msg.payload[tableDetails.tableKeys[0]]}};
