        return null;
    }
    let inoutMapping = SmartDevices.map( (ele) => {
		let tmp = {devName: "", pathWay: "", inpTopic: "", outTopic: "", sensor: false};
		tmp.devName = ele.devName;
		tmp.sensor = ele.sensor;
		tmp.pathWay = pathWays.unknown.name;
		if ( ele.mqtName.includes("esphome") ) {
			tmp.pathWay = pathWays.esphomeSmartthings.name;
		} else if ( ele.mqtName.includes("esp32") ) {
			tmp.pathWay = pathWays.esp32Smartthings.name;
		} else if ( ( ele.platform=="Tasmota" ) && (!ele.sensor) ) {
		    tmp.pathWay = pathWays.smartThingsTasmota.name;
        } else if ( ( ele.platform=="WLED" ) && (!ele.sensor) ) {
		    tmp.pathWay = pathWays.smartThingsWled.name;
		} else {
			if (!ele.Platform) tmp.pathWay =  pathWays.unknown.name;
		}
		if (tmp.pathWay == pathWays.esp32Smartthings.name ) {
			tmp.outTopic = "smartthings/" + ele.devName;
			if (ele.mqtName.includes("WEMO-04")) {
				tmp.outTopic = "smartthings/Swimming Pool";
			}
			tmp.inpTopic = ele.mqtName;
		} else if (tmp.pathWay == pathWays.esphomeSmartthings.name ) {
			tmp.outTopic = "smartthings/" + ele.devName;
			//} else if  (msg.topic.includes("xiaomib7_temperature/state")) pt1Topic = "smartthings/Garden Temperature/temperature"
			tmp.inpTopic = tmp.inpTopic =  ele.mqtName;
			if (ele.mqtName.includes("xiaomib7_temperature")) {
				tmp.outTopic = "smartthings/Garden Temperature/temperature";
			}
		} else if (tmp.pathWay == pathWays.smartThingsWled.name ) {
			tmp.inpTopic = "smartthings/" + ele.devName;
			tmp.outTopic = "wled/" + ele.mqtName
		} else if (tmp.pathWay == pathWays.smartThingsTasmota.name ) {
			tmp.inpTopic = "smartthings/" + ele.devName;
			tmp.outTopic = "Tasmota/" + ele.mqtName
		}
		return tmp;
	})
	SmartDevices.forEach( (ele) => {
		if (ele.idx) {
			inoutMapping.push({devName: ele.devName, pathWay: pathWays.smartThingsDomo.name, inpTopic: ele.mqtName, outTopic: ele.idx, sensor: true});
		}
	});
