				days: { max:8,  entries: [{time:rndTime(msg.payload.gmtime,60000 * 60 * 24),cnt: 0, gasMeter: 0, elecMeter:0 }] }
					}
}
function rndTime(time, ms) {
	return new Date(Math.floor(time/ms) * ms);
}
function addEntry(newObj, type = "minutes") {
	let ms = 60000;
	if (type == "minutes") ms = 60000;
	if (type == "hours") ms = 60000 * 60;
	if (type == "days") ms = 60000 * 60 * 24;
	newObj.time = rndTime(newObj.gmtime,ms);
	if (gDebug) node.warn([type + " On entry to addEntry ",GlowReadings[type],newObj])
	let unitsSinceStart = Math.floor( (newObj.time - GlowReadings[type].entries[0].time)/ms );
	if ( unitsSinceStart < 0 ) {
		node.error(["[" + node.name + "][Error]\t " + " unitsSinceStart negative " + unitsSinceStart + " No further processsing", GlowReadings[type].entries[0],newObj]);
		return null;
	}
	if (gDebug) node.warn([type + " Starting to add entry units=" + unitsSinceStart, GlowReadings[type].entries[unitsSinceStart],GlowReadings[type],newObj])
	while ((unitsSinceStart) && unitsSinceStart > GlowReadings[type].max) {
		GlowReadings[type].entries.shift();
		if (!GlowReadings[type].entries[0]) {
			node.warn([type + " big gap in messages. Setting entry 0 to this object", newObj, GlowReadings[type].entries[0],msg]);
			GlowReadings[type].entries[0] = newObj;
		}
		if (GlowReadings[type].entries[0].time) {
			unitsSinceStart = (GlowReadings[type].entries[0].time) ?
							Math.floor((newObj.time - GlowReadings[type].entries[0].time)/ms) :
							GlowReadings[type].max + 1;
		} else {
			node.error([type + " time is not set on entry 0 units="+unitsSinceStart ,GlowReadings[type].entries[0],GlowReadings[type],newObj])
			unitsSinceStart = (GlowReadings[type].entries[0].time) ?
							Math.floor((newObj.time - GlowReadings[type].entries[0].time)/ms) :
							0;
		}
	}
	if (gDebug) node.warn([type + " Adding entry units=" + unitsSinceStart, GlowReadings[type].entries[unitsSinceStart],GlowReadings[type],newObj])
	if ( GlowReadings[type].entries[unitsSinceStart] ) {
		let cnt = GlowReadings[type].entries[unitsSinceStart].cnt;
		if (cnt==0) {
			GlowReadings[type].entries[unitsSinceStart].gasMeter = newObj.gasMeter;
			GlowReadings[type].entries[unitsSinceStart].elecMeter = newObj.elecMeter;
			GlowReadings[type].entries[unitsSinceStart].gmtime = newObj.gmtime;
			GlowReadings[type].entries[unitsSinceStart].time = newObj.time;
		} else {
			GlowReadings[type].entries[unitsSinceStart].gasMeter = Math.round( (((GlowReadings[type].entries[unitsSinceStart].gasMeter * cnt) + newObj.gasMeter) / (cnt + 1)) * 10000 )/10000;
			GlowReadings[type].entries[unitsSinceStart].elecMeter = Math.round( (((GlowReadings[type].entries[unitsSinceStart].elecMeter * cnt) + newObj.elecMeter) / (cnt + 1)) * 10000 )/10000;
		}
		GlowReadings[type].entries[unitsSinceStart].cnt = GlowReadings[type].entries[unitsSinceStart].cnt + 1;	
	} else {
		GlowReadings[type].entries[unitsSinceStart] = newObj;
	}
	if (unitsSinceStart > 0) {
		cnt = unitsSinceStart-1;
		if (!GlowReadings[type].entries[cnt]) node.warn([type + " prev entry blank roll back units=" + unitsSinceStart, GlowReadings[type].entries[unitsSinceStart],GlowReadings[type],newObj])
		while (!GlowReadings[type].entries[cnt] && cnt>=0) {
			cnt--;
		}
		if (gDebug) if (cnt!= unitsSinceStart-1) node.warn([type + " Rolling forward after cnt=" + cnt, GlowReadings[type].entries[cnt],GlowReadings[type],newObj])
		for (let ind = cnt+1; ind < unitsSinceStart;ind++){
			if (!GlowReadings[type].entries[ind]) GlowReadings[type].entries[ind] = {};
			GlowReadings[type].entries[ind].gasMeter = GlowReadings[type].entries[cnt].gasMeter;
			GlowReadings[type].entries[ind].elecMeter = GlowReadings[type].entries[cnt].elecMeter;
			GlowReadings[type].entries[ind].cnt = 0;
			let wkDate = new Date(GlowReadings[type].entries[cnt].time);
			wkDate.setTime(wkDate.getTime() + ((ind-cnt) * ms));
			GlowReadings[type].entries[ind].time = wkDate;
			GlowReadings[type].entries[ind].gmtime = wkDate
			//GlowReadings[type].entries[ind].time = new Date().setTime(wkDate.getTime() + ((ind-cnt) * 60 * 1000))
			//GlowReadings[type].entries[ind].gmtime = wkDate.setTime(wkDate.getTime() + ((ind-cnt) * 60 * 1000))
			//GlowReadings[type].entries[ind].gmtime = new Date().setTime(wkDate.getTime() + ((ind-cnt) * 60 * 1000))
		}
		if (gDebug) node.warn([type + " About to set new obj units=" + unitsSinceStart, GlowReadings[type].entries[unitsSinceStart],GlowReadings[type],newObj])
		if ( ( GlowReadings[type].entries[unitsSinceStart].gasMeter - GlowReadings[type].entries[unitsSinceStart-1].gasMeter < 0 ) || 
			( GlowReadings[type].entries[unitsSinceStart].elecMeter - GlowReadings[type].entries[unitsSinceStart-1].elecMeter < 0 )) {
			node.error(["difference is less than 0 this ind=" + unitsSinceStart,GlowReadings[type].entries[unitsSinceStart],GlowReadings[type].entries[unitsSinceStart-1]]);
		}
	}
	if (gDebug) node.warn([type + " Added entry units=" + unitsSinceStart, GlowReadings[type].entries[unitsSinceStart],GlowReadings[type],newObj])
	return unitsSinceStart;
}
