		global.set("espDevices", espDevices);
		return [tableMsg, null, macMessages];
	} else {
		if (gDebug) node.warn( [ "[" + node.name + "][fromesp] " + " [Info] Unrecognised topic " + msg.topic + " nothing more to do",msg,topics]);
	}
} else if (topics[0] == "fromserver") {
