		return [null, newMsg, macMessages];
	} else {
		node.warn( [ "[" + node.name + "][fromserver] " + " [Info] Unrecognised topic " + msg.topic + " nothing more to do",msg,topics]);
	}
} else {
	node.warn( [ "[" + node.name + "] " + " [Info] unknown source (not fromesp or fromserver) ",msg,topics]);
