let MACs = global.get("MACs")
let lcase = 0;
let ucase = 0;
let both = 0;
let dups = {}
Object.keys(MACs).forEach( (mac) => {
	lcaseMac = mac.toLowerCase();
	ucaseMac = mac.toUpperCase();
	if (mac == lcaseMac) {
		lcase++;
		if (mac != ucaseMac) {
			if (MACs[ucaseMac]){
				both++;
				dups[ucaseMac] = MACs[mac];
			}
		}
	}
	if ( (mac == ucaseMac)  && (mac != lcaseMac) ) {
		ucase++;
		//if (MACs[lcaseMac]) both++;
	}
});
node.warn( [lcase + " lowercase macs, " + ucase + " uppercase macs, "  + both + " dups" , dups]);
return msg;

//BT Device handler
let gDebug = global.get("gDebug")
//gDebug = true;
function deriveSubType(name) {
    if (name) {
        if (name.includes("XMCT")) {
            return "bedside";
        } else if (name.includes("yeelight")) {
            return "candela";
        } else if (name.includes("ATC")) {
            return "atc";
        } else if (name.includes("ihoment")) {
            return "govee";
        } else {
            return "unknown";
        }
    }
    return "*blank*"
}
let BluetoothDeviceDetails = global.get("BluetoothDeviceDetails");
if (!BluetoothDeviceDetails) {
    node.error(["[" + node.name + "][Error] BluetoothDeviceDetails not set cannot continue"]);
    return null;
}
let MACs = global.get("MACs");
if (!MACs) {
    node.error(["[" + node.name + "][Error] MACs not set cannot continue"]);
    return null;
}

let espDevices = global.get("espDevices");
if (!espDevices) {
    node.error(["[" + node.name + "][Error] espDevices not set cannot continue"]);
    return null;
}
let macObject = null;
let devNameInput = msg.payload.deviceName;
let macInput = msg.payload.MAC;
let cmdInput = msg.payload.cmd;
if ((msg.hasOwnProperty("ui_control")) && (msg.ui_control.hasOwnProperty("callback")) &&
    (msg.ui_control.callback == "buttonClicked")) {
    if ((!msg.row.find(rw => rw.field == "mac")) || (!msg.ui_control.button)) {
        node.error(["[" + node.name + "][Error] Msg does have button or row", msg]);
        return null;
    }
    macInput = msg.row.find(rw => (rw.field == "mac")).val;
    cmdInput = msg.ui_control.button.toLowerCase();
    if (cmdInput == "pwron") cmdInput = "on";
    if (cmdInput == "pwroff") cmdInput = "off";
} else {
    if ((!msg.payload.deviceName) && (!msg.payload.MAC)) {
        if (gDebug) node.warn(["[" + node.name + "][Error] Payload does not specify a device or a MAC", msg]);
        return null;
    }
}
if (macInput) {
    macObject = MACs[macInput];
    node.warn(["DEBUG1 ", macInput, macObject, msg]);
} else {
    macObject = Object.values(MACs).find(dev => dev.deviceName == devNameInput);
    node.warn(["DEBUG2 ", macInput, macObject, msg]);
}
if (!macObject) {
    node.error(["[" + node.name + "][Error] BT Device not found - deviceName=" +
        devNameInput + " macInput=" + macInput, msg]);
    return null;
}
if (macObject.type != "BLE") {
    node.error(["[" + node.name + "][Error] BT Device not a BLE  type=" + macObject.type, msg, macObject]);
    return null;
}
if (gDebug) node.warn(macObject);
let BLEHosts = [];
Object.values(espDevices).forEach((esp) => {
    Object.values(esp.devices).forEach((dev) => {
        if (dev.address == macObject.MAC.toLowerCase()) {
            BLEHosts.push({ esp: esp.deviceName, RSSI: dev.RSSI, subType: deriveSubType(dev.name) });
        }
    });
});
if (BLEHosts.length == 0) {
    node.error(["[" + node.name + "][Error] No Hosts found", msg, macObject, espDevices]);
    return null;
}
BLEHosts.sort((a, b) => { return (a.RSSI > b.RSSI) ? -1 : 1 });
BLEHosts.forEach(host => node.warn(host.esp + " " + host.RSSI + " " + host.subType));
const BTDeets = BluetoothDeviceDetails[BLEHosts[0].subType];
if (!BTDeets) {
    if (gDebug) node.warn(["[" + node.name + "][Error] BT Device subtype does not exist in BT Details " + BLEHosts[0].subType, msg, macObject, BluetoothDeviceDetails]);
    return null;
}

const validCommand = BTDeets.validCommands.find(v => v.cmd == cmdInput);
if (!validCommand) {
    node.error(["[" + node.name + "][Error] " + cmdInput + " is not a valid commmand ", BTDeets, msg]);
    return null;
}
let blueToothMessage = {};
blueToothMessage.topic = "toesp/" + BLEHosts[0].esp + "/execute";
blueToothMessage.payload = {};
blueToothMessage.payload.cmd = cmdInput;
blueToothMessage.payload.mac = macObject.MAC;
blueToothMessage.payload.suuid = BTDeets.SERVICE_UUID;
blueToothMessage.payload.cuuid = BTDeets.CONTROL_UUID;
blueToothMessage.payload.nuuid = BTDeets.NOTIFY_UUID;
blueToothMessage.payload.commands = validCommand.bits[0];
blueToothMessage.payload.commands2 = validCommand.bits[1];

return blueToothMessage;

//Manage Network Devices
NetworkDevices = flow.get("NetworkDevices");
if (!NetworkDevices) {
	node.warn([ "[" + node.name + "] NetworkDevices does not exist - initialising"]);
	NetworkDevices = {};
}

if (msg.topic == "NetworkDevices_Dump") {
	node.warn([ "[" + node.name + "] Dumping Devices"]);
	return [null, {topic: "NetworkDevices_Dump", payload: NetworkDevices}];
}
const nowDate = new Date();
if ( (msg.topic=="listDevices") && (Array.isArray(msg.payload)) ) {
	listMsg = {topic: "listDevices", payload: msg.payload}
	Object.keys(NetworkDevices).forEach( (key) => {NetworkDevices[key].dropped = nowDate;});
	msg.payload.forEach( (dev) => {
		if (NetworkDevices[dev.mac]) {
			Object.keys(dev).forEach( (key) => {
				NetworkDevices[dev.mac][key] = dev[key];
			});
			if ( (!NetworkDevices[dev.mac].date) || ( ( nowDate - NetworkDevices[dev.mac].date) > (12 * 60 * 60 * 1000) ) ) {
				NetworkDevices[dev.mac].date = nowDate;
			}
