if (Message_Saves_Def.doSave) {
	let msg2 = null;
	let fileName = null;
	let rotateOn = 0;
	if (Message_Saves_Def.saveToFile) fileName = Message_Saves_Def.saveToFile;
	if (Message_Saves_Def.rotateOn) rotateOn = Message_Saves_Def.rotateOn;

	data.push(msg);
