	statusMsg = {fill:"green",shape:"ring",text: "Saving msgs to " + Message_Saves_Def.scope + 
						"." + Message_Saves_Def.variableName + " " + data.length + " currently saved"};
	if ( (rotateOn!=0) && ( (data.length >= rotateOn) || Message_Saves_Def.doRotate ) ) {
		let oldMessages = getData("old_" + Message_Saves_Def.variableName, Message_Saves_Def.scope);
		if ( (oldMessages) && (oldMessages.length>0) ) {
			let cnt = 1;
			let varName = "old_" + Message_Saves_Def.variableName + "_" + cnt;
			while  (getData(varName, Message_Saves_Def.scope)) {
				cnt++;
				varName = "old_" + Message_Saves_Def.variableName + "_" + cnt;
			}
			node.warn( "[" + node.name + "] Copying old_" + Message_Saves_Def.variableName + " to " + varName);
			setData(varName, oldMessages, Message_Saves_Def.scope);
		}
		node.warn( "[" + node.name + "] Saving " + Message_Saves_Def.variableName + " to " + "old_" + Message_Saves_Def.variableName);
		setData("old_" + Message_Saves_Def.variableName, data, Message_Saves_Def.scope);
		setData(Message_Saves_Def.variableName, [], Message_Saves_Def.scope);
		node.warn( "[" + node.name + "] Cleared " + Message_Saves_Def.variableName);
		if (fileName) {
			let baseName = path.join(path.dirname(fileName),path.basename(fileName,path.extname(fileName)))
			node.warn( [ "[" + node.name + "] " + "[Info] " + "Filename is " + fileName]);
			cnt = 0;
			let rotator = "_" + (++cnt) + path.extname(fileName);
			while (fs.existsSync(baseName + rotator)) {
				rotator = "_" + (++cnt) + path.extname(fileName);
			}
			try {
				if (fs.existsSync(fileName)) {
					node.warn( [ "[" + node.name + "] " + "[Info] ", 
							"Output File old file renamed from  " + fileName + "to " + baseName + rotator ]);
					fs.renameSync(fileName,baseName + rotator);
				}
			} catch(err) {
				node.warn( [err, "[" + node.name + "] " + "[Info] ", "Output File does not exist " + fileName ]);
			}
			msg2 = {topic:"", filename:fileName ,payload:oldMessages};
		}
	}
	node.status(statusMsg);
	if (Message_Saves_Def.doRotate) {
		Message_Saves_Def.doRotate = false;
		flow.set("Message_Saves_Def",Message_Saves_Def);
	}
	return [msg, msg2]
} else {
	statusMsg = {fill:"red",shape:"dot",text: "Saving Off to " + Message_Saves_Def.scope + 
						"." + Message_Saves_Def.variableName + " " + data.length + " currently saved"};
}
