    const active = Object.values(RoomMotions[smDevice.room].devices).reduce((pv,cv,ind) => { return ( pv||(cv.state=="active"))}, false);
    RoomMotions[smDevice.room].state = (active) ? "active" : "inactive";
    RoomMotions[smDevice.room].lastUpdated = nowDate;
    statusMsg = (active) ? {fill:"green",shape:"dot",text: smDevice.room + " is active"} : {fill:"blue",shape:"ring",text: smDevice.room + " is inactive"};
	node.status(statusMsg);
}
flow.set("RoomMotions",RoomMotions);
return msg;

//Pre Proc
let gDebug = global.get("gDebug")
let platforms = global.get("Platforms");
//Platform always the start
let topics = msg.topic.split("/");
