    if (gDebug) node.warn( [ "[" + node.name + "] " + " [Info] ", "Ignoring a node-red generated message",msg]);
    return [null,null];
}

if (topics[0] == "esphome") {
	if ( platform && platform.topicCommands && (topics.length > 3) ) {
		let found;
		try {
			found = platform.topicCommands.find( (tc) => topics[3].includes(tc));
		} catch (err) {
			debugText = (debugText == "") ? "espHome Catch " + err  : ";" + "espHome Catch " + err;
			node.error([ "[" + node.name + "][error] " + err, topics, msg]);
			found = false
		}
		if (found) {
			shortTopic = "esphome/" + topics[3].slice(0, topics[3].indexOf(found));
			payload.cmd = platform.parsedCommand[platform.topicCommands.indexOf(found)];
			if (!payload.cmd) {
				debugText = (debugText == "")
							? "Esphome ignoring " + topics.join("/") + " cmd not in pForm.parsed"
							: debugText + ";" + "Esphome ignoring " + topics.join("/") + " cmd not in pForm.parsed";

				if (gDebug) node.warn( [ "[" + node.name + "] " + " [Info] " + debugText,msg]);
				debugMessage.payload.push(debugText);
				return [debugMessage, null]
			}
			debugText = (debugText == "") ? "Processing esphome " + payload.cmd : debugText + ";" + "Processing esphome " + payload.cmd
			if (gDebug) node.warn( [ "[" + node.name + "] " + " [Info] " + debugText,msg]);
			debugMessage.payload.push(debugText);
			statusMsg = {fill:"green",shape:"dot",text: "esphome mapped to " + shortTopic};
			node.status(statusMsg)
			return [debugMessage, {topic: shortTopic, oldTopic: msg.topic, oldPayload: msg.payload, debugFileName: "/devices/preproc.csv", payload: payload}]
		} else {
			debugText = (debugText == "")
						? "Esphome ignoring Command not in pForm.topicCom " + topics.join("/")
						: ";" + "Esphome ignoring Command not in pForm.topicCom " + topics.join("/");
			if (gDebug) node.warn( [ "[" + node.name + "] " + " [Info] " + debugText,msg]);
			debugMessage.payload.push(debugText);
			statusMsg = {fill:"yellow",shape:"ring",text: "Command not in pForm " + msg.topic};
			node.status(statusMsg)
			return [debugMessage,null]
		}
	} else {
		debugText = (debugText == "")
					? "Esphome ignoring Platform/topics error " + topics.join("/")
					: ";" + "Esphome ignoring Platform/topics error " + topics.join("/");
		if (gDebug) node.warn( [ "[" + node.name + "] " + " [Info] " + debugText,msg]);
		debugMessage.payload.push(debugText);
		statusMsg = {fill:"yellow",shape:"ring",text: "Pform/topics error " + msg.topic};
		node.status(statusMsg)
		return [debugMessage,null]
	}
} else if (topics[0] == "fromesp") {
	//payload = {cmd: topics.slice(3).join("/"), payload: msg.payload};
	//shortTopic = topics.slice(0,3).join("/");
	if (topics.length>2) {
		payload = {cmd: topics.slice(3).join("/"), payload: msg.payload};
		shortTopic = "fromesp/" + topics[2];
	}
	debugText = (debugText == "") ?
							"Processing " + msg.topic + " " + msg.payload + " to " + shortTopic + " " + payload.cmd + ":" + payload.payload :
							debugText + ";" + "Processing " + msg.topic + " " + msg.payload + " to " + shortTopic + " " + payload.cmd + ":" + payload.payload;
	if (gDebug) node.warn( [ "[" + node.name + "] " + " [Info] " + debugText, msg]);
	debugMessage.payload.push(debugText);
	statusMsg = {fill:"green",shape:"dot",text: "fromesp mapped " + msg.topic + " to " + shortTopic};
	node.status(statusMsg)
	return [debugMessage, {topic:shortTopic, oldTopic: msg.topic, oldPayload: msg.payload, debugFileName: "/devices/preproc.csv", payload: payload}]
} else if (topics[0] == "toesp") {
	debugText = (debugText == "")
				? "Esphome ignoring toesp " + topics.join("/")
				: ";" + "Esphome ignoring toesp " + topics.join("/");
	if (gDebug) node.warn( [ "[" + node.name + "] " + " [Info] " + debugText,msg]);
	debugMessage.payload.push(debugText);
	statusMsg = {fill:"yellow",shape:"ring",text: "toesp ignored " + msg.topic};
	node.status(statusMsg)
	return [debugMessage,null]
}
debugText = (debugText == "") ? "Processing " + topics[0] + " " + payload.cmd : debugText + ";" + "Processing " + topics[0] + " " + payload.cmd
if (gDebug) node.warn( [ "[" + node.name + "] " + " [Info] " + debugText,msg]);
debugMessage.payload.push(debugText);
return [debugMessage, {topic:shortTopic, oldTopic: msg.topic, oldPayload: msg.payload, debugFileName: "/devices/preproc.csv", payload: payload}]

